mapper.select:
SELECT NVL(SUM(menu_price * amount), 0) money
FROM cart c, menu p
WHERE c.menu_no = p.menu_no AND c.member_email = #{member_email}

mapper.update:
		UPDATE cart    
        SET amount = #{amount} 
        WHERE member_email= #{member_email} 
        AND menu_no = #{menu_no}

mapper.select:
SELECT
COUNT(*)
FROM cart
WHERE
member_email= #{member_email}
AND menu_no = #{menu_no}

mapper.update:
	 UPDATE cart
        SET amount = amount + #{amount} 
        WHERE member_email = #{member_email} 
        AND menu_no = #{menu_no}
